import React, { useCallback, useEffect, useState } from "react";

import MoviesList from "./components/MoviesList";
import "./App.css";

function App() {
  const [movies, setMovies] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);
  const [cancel, isCancel] = useState(true);

  const fetchData = useCallback(async () => {
    setIsLoading(true);
    setError(null);
    try {
      const response = await fetch("https://swapi.dev/api/film");
      if (!response.ok) {
        isCancel(false);

        throw new Error("Something Went Wrong ....Retrying");
      }
      const data = await response.json();

      const movieData = data.results;
      const transform = movieData.map((obj) => {
        return {
          id: obj.episode_id,
          title: obj.title,
          openingText: obj.opening_crawl,
          releaseDate: obj.release_date,
        };
      });
      setMovies(transform);
      setIsLoading(false);
    } catch (error) {
      setIsLoading(false);
      setError(error.message);
      console.log(error);
    }
  }, []);

  setTimeout(
    useEffect(() => {
      if (!cancel) {
        fetchData();
      }
    }),
    5000
  );
  function stopCalling() {
    clearInterval();
    clearTimeout();
    useEffect(() => {}, []);
  }
  return (
    <React.Fragment>
      <section>
        <button onClick={fetchData}>Fetch Movies</button>
      </section>
      <section>
        <MoviesList movies={movies} />
        {isLoading && <h1 style={{ fontSize: "2.5rem" }}>Loading...</h1>}
        {!isLoading && error && <h1>{error}</h1>}
        <button onClick={stopCalling}>Cancel</button>
      </section>
    </React.Fragment>
  );
}

export default App;
